$(document).ready(function()
{
    $(".botao").click(function()
    {
        var value = $(this).attr('data-filter');
        
        if(value == "todos")
        {
            $('.filter').show('1000');
        }
        else
        {
            $(".filter").not('.'+value).hide('3000');
            $('.filter').filter('.'+value).show('3000'); 
        }
    });
    
    if ($(".botao").removeClass("active")) {
$(this).removeClass("active");
}
$(this).addClass("active");

});